  document.addEventListener('track:addToCart', function(event){
    var payload = event.detail
    var lineItems = JSON.parse(JSON.stringify(payload.line_items))
    var lineItemFromCart = {}
    lineItems.forEach(function(line_item){ if (line_item.variant_id === parseInt(payload.variant_id)){ lineItemFromCart = line_item } })
    dataLayer.push({ ecommerce: null })
    dataLayer.push({
      'event': 'addToCart',
      'ecommerce': {
        'currencyCode': lineItemFromCart.currency,
        'add': {
          'products': [{
            'name':          lineItemFromCart.name_for_gtm,
            'id':            lineItemFromCart.product_id,
            'price':         lineItemFromCart.simple_price,
            'originalPrice': lineItemFromCart.discounted,
            'discount':      (lineItemFromCart.simple_price - lineItemFromCart.discounted),
            'category':      lineItemFromCart.category_name,
            'quantity':      lineItemFromCart.quantity,
            'brand':         lineItemFromCart.designer_name
          }]
        }
      }
    });
  })
  
  document.addEventListener('track:saveRemoveFromCart', function(event){
    var lineItemFromCart = event.detail
    var lineItemObject   = {
      'event': 'removeFromCart',
      'ecommerce': {
        'currencyCode': lineItemFromCart.currency,
        'remove': {
          'products': [{
            'name':          lineItemFromCart.name_for_gtm,
            'id':            lineItemFromCart.product_id,
            'price':         lineItemFromCart.simple_price,
            'originalPrice': lineItemFromCart.discounted,
            'discount':      (lineItemFromCart.simple_price - lineItemFromCart.discounted),
            'category':      lineItemFromCart.category_name,
            'quantity':      lineItemFromCart.quantity,
            'brand':         lineItemFromCart.designer_name
          }]
        }
      }
    }
    var lineItemJSON = JSON.stringify(JSON.stringify(lineItemObject))
    localStorage.setItem('removeFromCartPage', lineItemJSON)
  })
  
  document.addEventListener('track:removeFromCart', function(event){
    var lineItemFromCart = event.detail
    dataLayer.push({ ecommerce: null })
    dataLayer.push({
      'event': 'removeFromCart',
      'ecommerce': {
        'currencyCode': lineItemFromCart.currency,
        'remove': {
          'products': [{
            'name':          lineItemFromCart.name_for_gtm,
            'id':            lineItemFromCart.product_id,
            'price':         lineItemFromCart.simple_price,
            'originalPrice': lineItemFromCart.discounted,
            'discount':      (lineItemFromCart.simple_price - lineItemFromCart.discounted),
            'category':      lineItemFromCart.category_name,
            'quantity':      lineItemFromCart.quantity,
            'brand':         lineItemFromCart.designer_name
          }]
        }
      }
    });
  })
  
  document.addEventListener('track:signup', function(event){
    setTimeout(function(){
      window.dataLayer.push({ event: "signup" })
    }, 200)
  })
  
  document.addEventListener('track:login', function(event){
    setTimeout(function(){
      window.dataLayer.push({ event: "login" })
    }, 200)
  })
</script>


