<script>
  var currentScrollPosition = 0;
  $('#close_wechat').click(function() {
    $('.wechat-modal').removeClass('show');
    toggleScrolling();
    currentScrollPosition = 0
  })
  
  $('#open_wechat').click(function() {
    currentScrollPosition = window.scrollY
    $('.wechat-modal').addClass('show');
    toggleScrolling();
  })
  
  function openWechat() {
    currentScrollPosition = window.scrollY
    $('.wechat-modal').addClass('show');
    toggleScrolling();
    return false;
  }
  
  function toggleScrolling() {
    if ($('.wechat-modal').hasClass('show')) {
      window.scrollTo(0, 0);
      $('body').css({
        overflow: 'hidden'
      });
      $('#content').css({
        opacity: '0.1'
      });
      content
    } else {
      window.scrollTo(0, currentScrollPosition);
      $('body').css({
        overflow: 'auto'
      });
      $('#content').css({
        opacity: '1'
      });
    }
  }
